$(document).ready(function(){$("h2").each(function(t){var i=$(this);i.closest("section").attr("id","link-"+t),$(".sidebar ol").append("<li><a id='link"+t+"' href='#link-"+t+"' title='"+i.attr("tagName")+"'>"+i.html()+"</a></li>")})}),$(document).ready(function(){function t(){var t=$(document).scrollTop();$(".sidebar > ol > li > a").each(function(){var i=$(this),o=$(i.attr("href"));o.position().top-5<=t&&o.position().top+o.height()>t?($(".sidebar > ol > li").removeClass("active"),i.parent().addClass("active")):i.parent().removeClass("active")})}$(".sidebar > ol > li > a").click(function(t){t.preventDefault();var i=$(this),o=$(i.attr("href")).position().top;$("body,html").animate({scrollTop:o},300)}),$(window).scroll(function(){t()})}),$(".btn-dropdown").click(function(t){$(this).parent().toggleClass("open")});